import React ,{ useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import Nav from './Nav';

export default function Index() {

    /************api */

    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        // Fetch data from the API when the component mounts
        fetch('https://api.pujakaitem.com/api/products')
          .then((response) => response.json())
          .then((data) => {
            setProducts(data);
            setLoading(false);
          })
          .catch((error) => {
            console.error('Error fetching data:', error);
            setLoading(false);
          });
      }, []);

      if (loading) {
        return <p>Loading...</p>;
      }

    /*********end api */
    return(
        <>
        <Nav/>

    <div class="main">

       
        <div className='serch-box'>
        <input className='search'placeholder='Search'/>
        </div>
        <div className='ad-space'>
        {products.map((product) => (
            <div class='card' key={product.id}>
                <div class='card-content'>
                    <img src={product.image} alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div> ))}
            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>
            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>

            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>
        </div>
        
    </div>
        </>
    )
}
