import React, { createContext, useContext, useReducer, useCallback } from 'react';

// Create a cart context
const CartContext = createContext();

// Define initial state for the cart
const initialCartState = [];
const initialFavState = [];

// Create a cart reducer to handle cart actions
const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      return [...state, action.payload];
    case 'REMOVE_FROM_CART':
      return state.filter(item => item.id !== action.payload.id);
    case 'CLEAR_CART':
      return [];
    default:
      return state;
  }
};
const favReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_FAV':
      return [...state, action.payload];
    case 'REMOVE_FROM_FAV':
      return state.filter(item => item.id !== action.payload.id);
    case 'CLEAR_FAV':
      return [];
    default:
      return state;
  }
};

// Create a CartProvider component to provide cart state and functions to child components
export const CartProvider = ({ children }) => {
  const [cart, dispatch] = useReducer(cartReducer, initialCartState);
  const [fav, spatch] = useReducer(favReducer, initialFavState);

  const addToCart = useCallback(item => {
    dispatch({ type: 'ADD_TO_CART', payload: item });
  }, []);

  console.log('caa',cart);

  const removeFromCart = useCallback(item => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: item });
  }, []);

  const clearCart = useCallback(() => {
    dispatch({ type: 'CLEAR_CART' });
  }, []);

  const addToFav = useCallback(item => {
    spatch({ type: 'ADD_TO_FAV', payload: item });
  }, []);

  const removeFromFav = useCallback(item => {
    spatch({ type: 'REMOVE_FROM_FAV', payload: item });
  }, []);

  const clearFav = useCallback(() => {
    spatch({ type: 'CLEAR_FAV' });
  }, []);

  const client = axios.create({
    baseURL: 'http://127.0.0.1:8000',
  });
  useEffect(() => {
    // Fetch cart data on component mount
    client.post('/api/cart/your_user_id/')  // Replace 'your_user_id' with the actual user ID
      .then(function (res) {console.log(res)})
      
      .catch(error => console.error('Error fetching cart data:', error));
  }, []);

  return (
    <CartContext.Provider value={{fav, cart, addToCart, removeFromCart, clearCart,addToFav, removeFromFav, clearFav }}>
      {children}
    </CartContext.Provider>
  );
};

