import './App.css';
import Cart from './components/Cart';
import Index from './components/Index';
import Product from './components/Product';
import Profile from './components/Profile';
import Singup from './components/Singup';

import Footer from './components/Footer';
import { BrowserRouter as Router, Routes, Route} from "react-router-dom";
import Admin from './components/Admin';
import Sale from './components/Sale';
import { ProductsProvider } from './context/ApiContext';
import Dyna from './components/Dyna';
import { AuthProvider } from './context/AuthContext';
import { CopnProvider } from './context/Copn';
import Singin from './components/Signin';
import { CartProvider } from './context/CartContext';

function App() {
  return (
    <>
   <Router>
   <ProductsProvider>
   <AuthProvider>
    <CopnProvider>
    <CartProvider>

        <Routes>
          <Route exact path="/" element={<Index/>}></Route>*/
          <Route exact path="/product/:productid" element={<Product/>}></Route>
          <Route exact path="/signup" element={<Singup/>}></Route>
          <Route exact path="/signin" element={<Singin/>}></Route>
          <Route exact path="/cart" element={<Cart/>}></Route>
          <Route exact path="/profile" element={<Profile/>}></Route>
          <Route exact path="/admin" element={<Admin/>}></Route>
          <Route exact path="/sale" element={<Sale/>}></Route>
          <Route exact path="/d/:productcato" element={<Dyna/>}></Route>
          </Routes>
      </CartProvider>
      </CopnProvider>
      </AuthProvider>
      </ProductsProvider>
      </Router>

    </>
  );
}

export default App;
