import React ,{ useState } from 'react'
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useProductsContext } from '../context/ApiContext';

export default function Admin() {

    const { isstaff } = useAuth();
    const { products, loading ,fetchProducts} = useProductsContext();

    const [productpg, setProductpg] = useState(false);
    const [userpg, setUserpg] = useState(false);
    const [padd, setPadd] = useState(false);
    const [pUpdate, setPUpdate] = useState(false);
    const [pDelete, setPDelete] = useState(false);
    const [searchInput, setSearchInput] = useState('');
    const [revalu,setRevalu]=useState([])
    const [opt,setOpt]=useState();



    const productchange =()=>{
        setProductpg(true)
        setUserpg(false)
    }

    const userchange =()=>{
        setProductpg(false)
        setUserpg(true)
    }

    const searchfun = () => {
        console.log(searchInput);
        const rv = products.filter((product) =>String(product[opt]) === searchInput);
        const rrv = rv.map((product) => ({
          "name": product.name,
          "price": product.price,
          "cato": product.cato,
          "sale": product.sale,
          "discount": product.discount,
          "size": product.size,
          "rating": product.rating,
          "dec": product.dec,
          "image": product.image,
          "imageone": product.imageone,
          "imagetwo": product.imagetwo,
          "imagethree": product.imagethree,
          
        }));
        const formattedDetails = JSON.stringify(rrv, null, 5); // Converts the array to a pretty-printed JSON string
        console.log(formattedDetails);
        setRevalu(formattedDetails);
      };
      
    
      const handleProductpost = async () => {
        try {
          const client = axios.create({
            baseURL: 'http://127.0.0.1:8000',
          });
      
          // Make the POST request
          const response = await client.post('/api/product/', revalu);
      
          // Log the response to the console
          console.log(response);
      
          // Fetch the updated product list
          fetchProducts();
      
          // Reset the form or do any other necessary actions
          setProductpg(false);
          console.log(revalu);
        } catch (error) {
          // Handle error
          console.error('Error adding product:', error);
        }
      };

    const handleProductposte =()=>{
        const client = axios.create({
            baseURL: 'http://127.0.0.1:8000',
          });
        client.post('/api/product/', revalu)
        setProductpg(false)
        console.log(revalu);
    }

    
    return(
        <>
        {isstaff ?(<><div className='nav'>
            <div className='container'>
                <h1>Admin Page</h1>

            </div>
            <div className='main-cols'>
                <button onClick={ productchange}>Product</button>
                <button onClick={ userchange}>User</button>
            </div>
        </div>
        
        {productpg &&(<>
        {/*<button onClick={ setPadd(true)} >Add</button><button onClick={ setPUpdate(true)}>Update</button><button onClick={ setPDelete(true )}>Delete</button>*/}
        <div className="serch-box">
            <select onChange={(e) => setOpt(e.target.value)}>
                <option></option>
                <option>id</option>
                <option>cato</option>
            </select>
          <input
            className="search"
            placeholder="Search"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
          />
          <p onClick={searchfun}>search</p>
        </div>
       
        <div className='ad-form'>
            <textarea className='ad-text' cols='10' rows="10"  value={revalu} onChange={(e) => setRevalu(e.target.value)}>
                
            </textarea>
                
                <button onClick={handleProductpost}>add</button>
            </div></>) }
            {userpg &&(
                <><form className='ad-form'>
                <div className='admin-lab'>
                    <h2>User Name</h2>
                    <input placeholder='item name'/>
                </div>
               
                
                <div className='admin-lab'>
                    <h2>Staff</h2>
                    <input type="checkbox"   />
                </div>
                
                
            </form></>
            )}
        </>):(
            <>  
        <div className='nav'>
            <div className='container'>
                <h1>Page Not Available</h1>

            </div>
            
        </div>
        </>
        )}</>
    )
}
