import React ,{ useState,useEffect } from 'react'
import { Link,useParams } from "react-router-dom";
import Nav from './Nav';

export default function Product({ product }) {
    const [activeIndex, setActiveIndex] = useState(0);
    const [prodata, setProdata] = useState(null);
    const { productid } = useParams();
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        // Fetch data from the API when the component mounts
        fetch('https://api.pujakaitem.com/api/products')
          .then((response) => response.json())
          .then((data) => {
            setProducts(data);
            setLoading(false);
          })
          .catch((error) => {
            console.error('Error fetching data:', error);
            setLoading(false);
          });
      }, []);
    // Verify if productData is available
    const productt = products.find((item) => item.id === productid);

    if (!productt) {
      return <p>Product not found.</p>;
    }

    
    

    const smallImgs = [
        product.image1,
        product.image2,
        product.image3];
    
      
    
      const handleSmallImgClick = (index) => {
        setActiveIndex(index);
      };

    return(
        <>
         <Nav/>
    
<section class="product-container main">
      
        <div class="img-card">
            <img id="featured-image"
        src={smallImgs[activeIndex]}
        alt="Featured Product" />
        
            <div class="small-Card">
            {smallImgs.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Small Product ${index}`}
            className={`small-Img ${index === activeIndex ? 'sm-card' : ''}`}
            onClick={() => handleSmallImgClick(index)}
          />))}
                </div>
        </div>
      
        <div class="product-info">
            <h3>{productt.name}</h3>
            <h5>price: <del>{productt.price}</del></h5>
            <p>{productt.description}</p>
            
            <div class="sizes">
                <p>Size:</p>
                <select name="Size" id="size" class="size-option">
                    <option value="xxl">XXL</option>
                    <option value="xl">XL</option>
                    <option value="medium">Medium</option>
                    <option value="small">Small</option>
                </select>
            </div>

            <div class="quantity">
                <input type="number" value="1" min="1"/>
                <button>Add to Cart</button>
            </div>

            <div className='cupon-box'>
                <input className='cupon' placeholder='Cupon Code' /><Link className='icon-btn'>Apply</Link>
            </div>
        </div>
    </section>





        </>
    )
}
