import React ,{ useState } from 'react'
import axios from 'axios';
import { useAuth } from '../context/AuthContext';
import { useProductsContext } from '../context/ApiContext';

export default function Admin() {

    const { isstaff } = useAuth();
    const { products, loading } = useProductsContext();

    const [productpg, setProductpg] = useState(false);
    const [userpg, setUserpg] = useState(false);
    const [padd, setPadd] = useState(false);
    const [pUpdate, setPUpdate] = useState(false);
    const [pDelete, setPDelete] = useState(false);
    const [searchInput, setSearchInput] = useState('');
    const [revalu,setRevalu]=useState([])

    const [pname,setPname]=useState();
    const [pdec,setPdec]=useState();
    const [pprice,setPprice]=useState();
    const [pcato,setPcato]=useState();
    const [pim,setPim]=useState();
    const [pimi,setPimi]=useState();
    const [pimii,setPimii]=useState();
    const [pimiii,setPimiii]=useState();
    const [psale, setPsale] = useState(false);
    const [pfea, setPfea] = useState(false);
    const [pdis,setPdis]=useState();

    const productchange =()=>{
        setProductpg(true)
        setUserpg(false)
    }

    const userchange =()=>{
        setProductpg(false)
        setUserpg(true)
    }

    const searchfun = () => {
        const rv = products.filter((product) => product.cato === searchInput);
        const rrv = rv.map((product) => ({
          "name": product.id,
          "price": product.price,
          "dec": product.dec,
          "cato": product.cato,
          "sale": product.sale,
          "discount": product.discount,
          "size": product.size,
          "rating": product.rating,
          "image": product.image,
          "imageone": product.imageone,
          "imagetwo": product.imagetwo,
          "imagethree": product.imagethree,
          "************":"************",
          
        }));
        const formattedDetails = JSON.stringify(rrv, null, 5); // Converts the array to a pretty-printed JSON string
        console.log(formattedDetails);
        setRevalu(formattedDetails);
      };
      
    
    

    const handleProductpost =()=>{
        const client = axios.create({
            baseURL: 'http://127.0.0.1:8000',
          });
        const data ={
            "name": pname,
        "price": pprice,
        "dec": pdec,
        "image": pim,
        "imageone": pimi,
        "imagetwo": pimii,
        "imagethree": pimiii,
        "cato": pcato,
        "sale": psale,
        "discount": pdis,
        "size":"x",
        "rating":5
        }
        console.log('dtataa',data);
        client.post('/api/product/', data)
        setProductpg(false)
    }

    
    return(
        <>
        {isstaff ?(<><div className='nav'>
            <div className='container'>
                <h1>Admin Page</h1>

            </div>
            <div className='main-cols'>
                <button onClick={ productchange}>Product</button>
                <button onClick={ userchange}>User</button>
            </div>
        </div>
        <div className='main'>
        {productpg &&(<>
        {/*<button onClick={ setPadd(true)} >Add</button><button onClick={ setPUpdate(true)}>Update</button><button onClick={ setPDelete(true )}>Delete</button>*/}
        <div className="serch-box">
          <input
            className="search"
            placeholder="Search"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
          />
          <p onClick={searchfun}>search</p>
        </div>
       
        <div className='ad-form'>
            <textarea className='ad-text' cols='10' rows="10" style={{'width': '321px', 'height': '223px' }} value={revalu}>
                
            </textarea>
                {/*
                <div className='admin-lab'>
                    <h2>Product Name</h2>
                    <input placeholder='item name'  onChange={(e) => setPname(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Product Dec</h2>
                    <input placeholder='item dec'   onChange={(e) => setPdec(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Product Price</h2>
                    <input placeholder='item price'  onChange={(e) => setPprice(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Product Cato</h2>
                    <input placeholder='item Cato'  onChange={(e) => setPcato(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Product Image 1</h2>
                    <input type="text" placeholder='itemimage'  onChange={(e) => setPim(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Product Image 2</h2>
                    <input type="text" placeholder='itemimage'  onChange={(e) => setPimi(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Product Image 3</h2>
                    <input type="text" placeholder='itemimage'  onChange={(e) => setPimii(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Product Image 4</h2>
                    <input type="text" placeholder='itemimage'  onChange={(e) => setPimiii(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Sale</h2>
                    <input type="checkbox"    checked={psale} onChange={(e) => setPsale(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Featured</h2>
                    <input type="checkbox"   checked={pfea} onChange={(e) => setPfea(e.target.value)}/>
                </div>
                <div className='admin-lab'>
                    <h2>Discount</h2>
                    <input type="text"    onChange={(e) => setPdis(e.target.value)}/>
                </div>*/}
                <button onClick={handleProductpost}>add</button>
            </div></>) }
            {userpg &&(
                <><form className='ad-form'>
                <div className='admin-lab'>
                    <h2>User Name</h2>
                    <input placeholder='item name'/>
                </div>
               
                
                <div className='admin-lab'>
                    <h2>Staff</h2>
                    <input type="checkbox"   />
                </div>
                
                
            </form></>
            )}
        </div></>):(
            <>  
        <div className='nav'>
            <div className='container'>
                <h1>Page Not Available</h1>

            </div>
            
        </div>
        </>
        )}</>
    )
}
