import React ,{ useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import Nav from './Nav';

export default function Index() {

    /************api */

    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    useEffect(() => {
        // Fetch data from the API when the component mounts
        fetch('https://api.pujakaitem.com/api/products')
          .then((response) => response.json())
          .then((data) => {
            setProducts(data);
            setLoading(false);
          })
          .catch((error) => {
            console.error('Error fetching data:', error);
            setLoading(false);
          });
      }, []);

      if (loading) {
        return <p>Loading...</p>;
      }

    /*********end api */

    /****Search function */
    const [searchInput, setSearchInput] = useState('');
    const [filterCategory, setFilterCategory] = useState('');
    const filteredProducts = products.filter((product) => {
        // Apply the search filter
        const productName = product.name.toLowerCase();
        const category = product.category.toLowerCase();
    
        if (searchInput && productName.indexOf(searchInput.toLowerCase()) === -1) {
          return false;
        }
    
        // Apply the category filter
        if (filterCategory && product.category !== filterCategory) {
          return false;
        }
    
        return true;
      });

    /*****end search */

    return(
        <>
        <Nav/>

    <div class="main">

       
        <div className='serch-box'>
        <input className='search'placeholder='Search' /* from search*/value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}/**end**/ />
          {/**from search  */}
          <select
          value={filterCategory}
          onChange={(e) => setFilterCategory(e.target.value)}
        >
          <option value="">All Categories</option>
          {/* Add options for available categories */}
        </select>
           {/**end search  */}
        </div>
        <div className='ad-space'>
        {products.map((product) => (
            <div class='card' key={product.id}>
                <div class='card-content'>
                    <img src={product.image} alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div> ))}
            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>
            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>

            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>
        </div>
        
    </div>
        </>
    )
}
