  const login = (ldata) => {
    
    const emailMatch = userData.find((item) => item.email === ldata.name && item.password === ldata.password);
    if (emailMatch) {
      // You found a matching user, so you can access its properties directly
      const dts = emailMatch;
      setSuserData(dts);
      console.log('Suser', suserData);
      setIsLoggedIn(true);
      alert('User logged in');
    } else {
      alert('Invalid email or password');
      setIsLoggedIn(false);
    }
  };

  const logout = () => {
    setIsLoggedIn(false);
    alert('You Have Been Logout')
  };

  const updateislog=(newvalue)=>{
    setIsLoggedIn(newvalue)
  }

 

  const updateData=(udata) =>{
    const jsonData = JSON.stringify(udata);

    fetch('/api/users', {
      method: 'PATCH',
      headers: {
        'Content-Type': 'application/json',
      },
      body: jsonData,
    })
    .then((response) => {
      if (response.ok) {
        return response.json(); // Parse the response JSON if needed
      }
      throw new Error('Network response was not ok.');
    })
    .then((data) => {
      // Handle success (e.g., display a success message)
      console.log('User added successfully:', data);
    })
    .catch((error) => {
      // Handle errors (e.g., display an error message)
      console.error('Error adding user:', error);
    });

  }

  return (
    <AuthContext.Provider value={{ isLoggedIn,suserData, login, logout,updateislog }}>
      {children}
    </AuthContext.Provider>
  );
  }
