import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import Nav from './Nav';
import { useProductsContext } from '../context/ApiContext';
import { useCopnContext } from '../context/Copn';

export default function Product({ product }) {
  const [activeIndex, setActiveIndex] = useState(0);
  const [couponInput, setCouponInput] = useState('');
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const { productid } = useParams();
  const { products, loading } = useProductsContext();
  const { couponData } = useCopnContext();

  const pid = parseInt(productid);
  const productt = products?.find((item) => item.id === pid);

  if (!productt) {
    return <p>Product not found.</p>;
  }

  if (loading) {
    return (
      <section className="lor-layout">
        <div className="lor-header"></div>
        <div className="lor-main">
          <span className="loader">Loading</span>
        </div>
        <div className="lor-footer"></div>
      </section>
    );
  }

  const smallImgs = [
    productt.image,
    productt.imageone,
    productt.imagetwo,
    productt.imagethree,
  ];

  const handleSmallImgClick = (index) => {
    setActiveIndex(index);
  };

  const applyCoupon = () => {
    if (!couponData) {
      // Handle the case where couponData is not available yet
      alert('Coupon data is not available. Please wait.');
      return;
    }

    const coupon = couponData.find((item) => item.code === couponInput);

    if (coupon) {
      setAppliedCoupon(coupon);
    } else {
      alert('Invalid coupon code');
    }
  };

  return (
    <>
      <Nav />

      <section className="product-container main">
        <div className="img-card">
          <img
            id="featured-image"
            src={smallImgs[activeIndex]}
            alt="Featured Product"
          />

          <div className="small-Card">
            {smallImgs.map((img, index) => (
              <img
                key={index}
                src={img}
                alt={`Small Product ${index}`}
                className={`small-Img ${index === activeIndex ? 'sm-card' : ''}`}
                onClick={() => handleSmallImgClick(index)}
              />
            ))}
          </div>
        </div>

        <div className="product-info">
          <h3>{productt.name}</h3>
          {appliedCoupon && (
            <h5>
              price: <del>{productt.price}</del>
              {appliedCoupon.price}
            </h5>
          )}
          <p>{productt.description}</p>

          <div className="sizes">
            <p>Size:</p>
            <select name="Size" id="size" className="size-option">
              <option value="xxl">XXL</option>
              <option value="xl">XL</option>
              <option value="medium">Medium</option>
              <option value="small">Small</option>
            </select>
          </div>

          <div className="quantity">
            <input type="number" value="1" min="1" />
            <button>Add to Cart</button>
          </div>

          <div className="cupon-box">
            <input
              className="cupon"
              value={couponInput}
              onChange={(e) => setCouponInput(e.target.value)}
              placeholder="Coupon Code"
            />
            <Link onClick={applyCoupon} className="icon-btn">
              Apply
            </Link>
          </div>
        </div>
      </section>
    </>
  );
}
