import React, { createContext, useContext, useState ,useEffect } from 'react';

const AuthContext = createContext();

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [userData, setUserData] = useState(null);
  const [suserData, setSuserData] = useState(null);
  useEffect(() => {
    // Fetch data from the API when the component mounts
    fetch('http://127.0.0.1:8000/api/user/')
      .then((response) => response.json())
      .then((data) => {
        setUserData(response.data);
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
      });
      

     
  }, []);

  const login = (ldata) => {
    if (userData?.find((item) => item.name === ldata)&&(userData?.find((item) => item.password === ldata.password))){
      const dts = userData?.find((item) => item.name === ldata);
      setSuserData(dts)
    setIsLoggedIn(true);}
    else{
      alert('wrong user/password')
    }
  };

  const logout = () => {
    // Implement your logout logic
    setIsLoggedIn(false);
    alert('You Have Been Logout')
  };

  const updateislog=(newvalue)=>{
    setIsLoggedIn(newvalue)
  }

  return (
    <AuthContext.Provider value={{ isLoggedIn,suserData, login, logout,updateislog }}>
      {children}
    </AuthContext.Provider>
  );
}
