import React ,{ useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import Nav from './Nav';
import { useProductsContext  } from '../context/ApiContext';
import { useCopnContext  } from '../context/Copn';

export default function Index() {
    const { products, loading } = useProductsContext();
    /************api */
    const [searchInput, setSearchInput] = useState('');

    const { couponData } = useCopnContext();
      if (loading) {
        return (
        <>
        <section className="lor-layout">
  <div className="lor-header"></div>
  <div className="lor-main"><span className="loader">Loading</span></div>
  <div className="lor-footer"></div>
</section></>)
      }


      

    /*********end api */

    /****Search function */

    const filteredProducts = products.filter((product) => {
        // Apply the search filter
        const productName = product.name.toLowerCase();
    
        return searchInput === '' || productName.indexOf(searchInput.toLowerCase()) !== -1;
      });

    /*****end search */

  

    return(
        <>
        <Nav/>

    <div className="main">

       
        <div className='serch-box'>
        <input className='search'placeholder='Search' /* from search*/value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}/**end**/ />
          
        </div>
        <div className='ad-space'>
        {filteredProducts.map((product) => (
            <div className='card' key={product.id}>
                <div className='card-content'>
                    <img src={product.image} alt='Product Image'/>
                    
                </div>
                <Link className='btn' to={{
    pathname: `/product/${product.id}`,
    state: { productData: product }
  }}>Shop</Link>
            </div> ))}
            <div className='card'>
                <div className='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link className='btn'>Shop</Link>
            </div>
            <div className='card'>
                <div className='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link className='btn'>Shop</Link>
            </div>

            <div className='card'>
                <div className='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link className='btn'>Shop</Link>
            </div>
        </div>
        
    </div>
        </>
    )
}
