import React ,{ useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import Nav from './Nav';
import { useProductsContext  } from '../context/ApiContext';
import {useCart} from '../context/CartContext'
import { useAuth } from '../context/AuthContext';
import { useSaleContext } from '../context/SaleContext';

export default function Sale() {

    /************api */
    const [searchInput, setSearchInput] = useState('');
    const { products, loading } = useProductsContext();
    const { addToFav } = useCart();
    const { saleinfo,sale,sLoading} = useSaleContext();
    const [currentDate, setCurrentDate] = useState(new Date());
    const [sale_data,setSale_data]=useState()
    const [sale_title,setSale_title]=useState()
    const [issale,setIssale]=useState(false)
    
  
    const intervalId = setInterval(() => {
      setCurrentDate(new Date());
    });
     /* sale data*/
     
    
    // Run the fetchData function once when the component mounts
  // Empty dependency array means the effect runs once on mount
    

 
  if (sLoading) {

    // Handle the case where saleinfo is not available yet
    return <div>Loading...</div>;
    
  }
 else{
  console.log('function......');
    
      const formattedDate = currentDate.toLocaleDateString('en-CA', { year: 'numeric', month: '2-digit', day: '2-digit' });
      
      const sale_date = saleinfo.filter((find) => find.start_date === '2023-12-27');
        setSale_title(sale_date.name)

 }
  


  
  if (loading) {
        
        return (
        <>
        <section className="lor-layout">
  <div className="lor-header"></div>
  <div className="lor-main"><span className="loader">Loading</span></div>
  <div className="lor-footer"></div>
</section></>)
      }

      
      

    /*********end api */

    /****Search function */

    const filteredProducts = products.filter((product) =>product.sale === true);


    /*sale_date.map((product) => (
      /*console.log('name:',product.name)
    ))*/

    /*****end search */

    const addtoFavl =(dp)=>{
        let itemToAdd;
       
          
          itemToAdd = {
            id: dp.id,
            price: dp.price,
          image:dp.image,
          name:dp.name,
    
        }
        console.log(itemToAdd);
        alert(`${dp.name} Added to fav`)
        addToFav(itemToAdd);
        }

       

    return(
        <>
        <Nav/>

    <div className="main">

       
        <div className='serch-box'>
        <h1 >{sale_title}</h1>
        </div>
        <div className='ad-space'>
        {filteredProducts.map((product) => (
            <div className='card' key={product.id}>
                <div className='card-content'>
                <Link   className='fav-btn icon-btn' onClick={() => {  addtoFavl(product); }}><span className="material-symbols-outlined">favorite</span></Link>
                    <img src={product.image} alt='Product Image'/>
                    
                </div>
                <Link className='btn' to={{
    pathname: `/product/${product.id}`,
    state: { productData: product }
  }}>Shop</Link>
            </div> ))}
            

            
        </div>
        
    </div>
        </>
    )
}
