import React ,{ useState, useEffect } from 'react'
import { Link ,useParams } from "react-router-dom";
import Nav from './Nav';
import { useProductsContext  } from '../context/ApiContext';

export default function Dyna() {
    const  productcato = useParams();
    /************api */
    const [searchInput, setSearchInput] = useState('');
    const { products, loading } = useProductsContext();

      if (loading) {
        return (
        <>
        <section class="lor-layout">
  <div class="lor-header"></div>
  <div class="lor-main"><span class="loader">Loading</span></div>
  <div class="lor-footer"></div>
</section></>)
      }


      
      const productt = products.find((item) => item.category === productcato);
    /*********end api */

    

  

    return(
        <>
        <Nav/>

    <div class="main">

       
        <div className='serch-box'>
        <input className='search'placeholder='Search' /* from search*/value={searchInput}
          onChange={(e) => setSearchInput(e.target.value)}/**end**/ />
          
        </div>
        <div className='ad-space'>
        {productt.map((product) => (
            <div class='card' key={product.id}>
                <div class='card-content'>
                    <img src={product.image} alt='Product Image'/>
                    
                </div>
                <Link class='btn' to={{
    pathname: `/product/${product.id}`,
    state: { productData: productt }
  }}>Shop</Link>
            </div> ))}
            
        </div>
        
    </div>
        </>
    )
}
