import React, { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';
import Nav from './Nav';
import { useProductsContext } from '../context/ApiContext';
import { useCart } from '../context/CartContext'

export default function Dyna() {
  const { productcato } = useParams();
  const { addToFav } = useCart();
  const [searchInput, setSearchInput] = useState('');
  const { products, loading } = useProductsContext();
  const [productt, setProductt] = useState('');
  

  if (loading) {
    return (
      <div className="lor-layout">
        <div className="lor-header"></div>
        <div className="lor-main">
          <span className="loader">Loading</span>
        </div>
        <div className="lor-footer"></div>
      </div>
    );
  }

  const dproduct = products.filter((product) => product.cato === productcato);
  const addtoFavl =()=>{
    let itemToAdd;
   
      
      itemToAdd = {
        id: productt.id,
        price: productt.price,
      image:productt.image,
      name:productt.name,

    }
    alert(`${productt.name} Added to fav`)
    addToFav(itemToAdd);
    }

  return (
    <>
      <Nav />

      <div className="main">
        <div className="serch-box">
          <input
            className="search"
            placeholder="Search"
            value={searchInput}
            onChange={(e) => setSearchInput(e.target.value)}
          />
        </div>
        <div className="ad-space">
          {dproduct.length === 0 ? (
            <p>No products found in this category.</p>
          ) : (
            dproduct.map((product) => (
              
              <div className="card" key={product.id}>
                <Link   className='fav-btn icon-btn' onClick={() => { setProductt(product); addtoFavl(); }}><span className="material-symbols-outlined">favorite</span></Link>
                <div className="card-content">
                  <img src={product.image} alt="Product Image" />
                </div>
                <Link
                  className="btn"
                  to={{
                    pathname: `/product/${product.id}`,
                    state: { productData: product },
                  }}
                >
                  Shop
                </Link>
              </div>
            ))
          )}
        </div>
      </div>
    </>
  );
}
