import React, { createContext, useContext, useState, useEffect } from 'react';
import Cookies from 'js-cookie';
import axios from 'axios';


const SaleContext = createContext();
const AuthContext = createContext();
axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';
axios.defaults.withCredentials = false;

export function useSaleContext() {
  return useContext(SaleContext);
}

export function SaleProvider({ children }) {


    const client = axios.create({
        baseURL: 'http://127.0.0.1:8000',
      });
    const [saleinfo,setSaleinfo]=useState('')
    const [sLoading, setSLoading] = useState(true);
    const [currentDate, setCurrentDate] = useState(new Date());
    const [sale_data,setSale_data]=useState()
    const [sale_title,setSale_title]=useState()
    const storedToken = Cookies.get('token');
    const intervalId = setInterval(() => {
        setCurrentDate(new Date());
      });

    function sale() {
        client.get('/api/sale/',).then(function(res){
          console.log(res.data)
          setSaleinfo(res.data)
          
        }).then(function(res){
            console.log(saleinfo);
            //const sale_dat = saleinfo.find((e) =>e.start_date === '2023-12-27');
            //setSale_title(sale_dat.name)
        })
        console.log(saleinfo)
      }
  
  


  

  function salepsot(a) {
    const storedToken = Cookies.get('token');
    const accessToken = JSON.parse(storedToken).access;
    client.post('/api/sale/',a,{ credentials:'include',headers: {
      Authorization: `Bearer ${accessToken}`,
    },}).then(function(res){
      console.log(res)
    })
  }


  
  function salepatcht(a) {
    const storedToken = Cookies.get('token');
    const accessToken = JSON.parse(storedToken).access;
    client.patch('/api/sale/',a,{ credentials:'include',headers: {
      Authorization: `Bearer ${accessToken}`,
    },}).then(function(res){
      console.log(res)
    })
  }

  function saledeletet(a) {
    const storedToken = Cookies.get('token');
    const accessToken = JSON.parse(storedToken).access;
    client.delete('/api/sale/',a,{ credentials:'include',headers: {
      Authorization: `Bearer ${accessToken}`,
    },}).then(function(res){
      console.log(res)
    })
  }
  const fetchSale = async () => {
    try {
      setSLoading(true);
      const response = await fetch('http://127.0.0.1:8000/api/sale/',);
      const data = await response.json();

      setSaleinfo(data);
      setSLoading(false);
      const sale_dat = saleinfo.find((e) =>e.start_date === '2023-12-27');
    
      setSale_title(sale_dat.name)
      return data; // Return the fetched data
    } catch (error) {
      console.error('Error fetching data:', error);
      setSLoading(false);

      // Reject with the error
      throw error;
    }
  };
  useEffect(() => {
    
    sale()
      
   }, [saleinfo]);

   
   

   
  const value = { saleinfo,sLoading,saledeletet,salepsot,sale_title,salepatcht,};


  return <SaleContext.Provider value={value}>{children}</SaleContext.Provider>;
}
