import React ,{ useState,useEffect } from 'react'
import { Link } from "react-router-dom";

export default function Product() {

    const [sidePanelWidth, setSidePanelWidth] = useState('none');

    const openNav = () => {
        if (window.innerWidth <= 500) {
            setSidePanelWidth('block');
        }
    };

    const closeNav = () => {
        if (window.innerWidth <= 500) {
            setSidePanelWidth('none');
        }
    };

    useEffect(() => {
        // Check the window width on initial render and whenever the window is resized
        const handleWindowResize = () => {
            if (window.innerWidth <= 500) {
                setSidePanelWidth('none');
            } else {
                setSidePanelWidth('block');
            }
        };

        // Add an event listener for window resize
        window.addEventListener('resize', handleWindowResize);

        // Check the window width on initial render
        handleWindowResize();

        // Remove the event listener when the component unmounts
        return () => {
            window.removeEventListener('resize', handleWindowResize);
        };
    }, []);


    const smallImgs = [
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955',
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-6_360x.jpg?v=1695035964',
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-5-27529864151190_360x.jpg?v=1695035962',
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955',
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035957',
      ];
    
      const [activeIndex, setActiveIndex] = useState(0);
    
      const handleSmallImgClick = (index) => {
        setActiveIndex(index);
      };

    return(
        <>
         <div className='sidebar sidepanel' id="mySidepanel" style={{ display: sidePanelWidth  }}>
            <button href="javascript:void(0)" className="closebtn" onClick={closeNav}>×</button>
                <div class="side-head">
                    <p>Poshak</p>
                </div>
                <hr className='hr'></hr>
                <div className='col-nav'>
                    <ul className='col-navul'>
                        <li className='col-nav-itm'><Link to='/'>Cloth</Link></li>
                        <li className='col-nav-itm'><Link>ReaduMade</Link></li>
                        <li className='col-nav-itm'><Link>Sarees</Link></li>
                    </ul>
                </div>
                <div className='side-footer'>
                    <div className='m3'>
                        <Link className='s-btn'>Login</Link>
                    </div>
                    
                </div>
            </div>
<div className='nav'>
            <div className='container'>
                <div><button className="openbtn" onClick={openNav}>☰ </button></div>
            
                <div>
                <Link to='/profile' className='icon-btn'><span class="material-symbols-outlined">person</span></Link><Link to='/fav' className='icon-btn'><span class="material-symbols-outlined">favorite</span></Link><Link to='/cart' className='icon-btn'><span class="material-symbols-outlined">shopping_cart</span></Link>
                </div>
            </div>
            <div className='main-cols'>
            <Link to='/'>New</Link><Link>sale</Link><Link>Extra</Link>
            </div>
        </div>
    
<section class="product-container">
      
        <div class="img-card">
            <img id="featured-image"
        src={smallImgs[activeIndex]}
        alt="Featured Product" />
        
            <div class="small-Card">
            {smallImgs.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Small Product ${index}`}
            className={`small-Img ${index === activeIndex ? 'sm-card' : ''}`}
            onClick={() => handleSmallImgClick(index)}
          />))}
                </div>
        </div>
      
        <div class="product-info">
            <h3>LEVI'S® WOMEN'S XL TRUCKER JACKET</h3>
            <h5>Price: $140 <del>$170</del></h5>
            <p>tae corporis veniam atque facilis, et alias eos ullam nesciunt voluptatum.</p>
            
            <div class="sizes">
                <p>Size:</p>
                <select name="Size" id="size" class="size-option">
                    <option value="xxl">XXL</option>
                    <option value="xl">XL</option>
                    <option value="medium">Medium</option>
                    <option value="small">Small</option>
                </select>
            </div>

            <div class="quantity">
                <input type="number" value="1" min="1"/>
                <button>Add to Cart</button>
            </div>

            <div className='cupon-box'>
                <input className='cupon' placeholder='Cupon Code' /><Link className='icon-btn'>Apply</Link>
            </div>
        </div>
    </section>





        </>
    )
}
