import React ,{ useState, useEffect } from 'react'
import { Link } from "react-router-dom";
import Nav from './Nav';

export default function Index() {

    const [sidePanelWidth, setSidePanelWidth] = useState('none');

    const openNav = () => {
        if (window.innerWidth <= 500) {
            setSidePanelWidth('block');
        }
    };

    const closeNav = () => {
        if (window.innerWidth <= 500) {
            setSidePanelWidth('none');
        }
    };

    useEffect(() => {
        // Check the window width on initial render and whenever the window is resized
        const handleWindowResize = () => {
            if (window.innerWidth <= 500) {
                setSidePanelWidth('none');
            } else {
                setSidePanelWidth('block');
            }
        };

        // Add an event listener for window resize
        window.addEventListener('resize', handleWindowResize);

        // Check the window width on initial render
        handleWindowResize();

        // Remove the event listener when the component unmounts
        return () => {
            window.removeEventListener('resize', handleWindowResize);
        };
    }, []);


    return(
        <>
        <Nav/>

    <div class="main">

       
        <div className='serch-box'>
        <input className='search'placeholder='Search'/>
        </div>
        <div className='ad-space'>
            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>
            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>
            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>

            <div class='card'>
                <div class='card-content'>
                    <img src='https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955' alt='Product Image'/>
                    
                </div>
                <Link class='btn'>Shop</Link>
            </div>
        </div>

    </div>
        </>
    )
}
