import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import Cookies from 'js-cookie';

const AuthContext = createContext();
axios.defaults.xsrfCookieName = 'csrftoken';
axios.defaults.xsrfHeaderName = 'X-CSRFToken';
axios.defaults.withCredentials = false;

const client = axios.create({
  baseURL: 'http://127.0.0.1:8000',
});

export function useAuth() {
  return useContext(AuthContext);
}

export function AuthProvider({ children }) {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [suserData,setSuserData]=useState('');
  

  useEffect(() => {
    const storedToken =localStorage.getItem('token');
    if (storedToken) {
    
      setIsLoggedIn(true);
      getuser()
    }
    
  }, []);

  function submitUser(e) {
    console.log(e);
    client.post('/api/register/', e).then(function (res) {
      submitlogin(e);
    });
  }

  

function getuser(){
  const authToken = Cookies.get('authToken');
  console.log(`Bearer ${authToken}`);
    client.get('/api/user/',{ credentials:'include',headers: {
      Authorization: `Bearer ${authToken}`,
    },})
      .then(function (res) {
        console.log('***********ttt user data***********',res);
        setIsLoggedIn(true);
        setSuserData(res.data);
      })
      .catch(function (error) {
        console.log('***********eorror user data***********');
        console.error('Error fetching user data:', error);
        //setSuserData('');
        //setIsLoggedIn(false);
        //window.localStorage.token=''
      });
  }
  

  function submitlogin(e) {
  
    client.post('/api/login/', e,{ maxRedirects: 0 }).then(function (res) {
      console.log(res);
     
      const token = res.data.token;
      Cookies.set('authToken', token, { expires: 1 });
      
      setIsLoggedIn(res.data.login);
      getuser()
    });
  }



  function submitlogout(e) {
    
    client.post('/api/logout/', {}).then(function (res) {
      Cookies.remove('authToken');
      
      setIsLoggedIn(false);
    });
  }

  const updateislog=(newvalue)=>{
    setIsLoggedIn(newvalue)
  }




  
  return (
    <AuthContext.Provider value={{ isLoggedIn,submitUser,suserData, submitlogin, submitlogout ,updateislog}}>
      {children}
    </AuthContext.Provider>
  );
  }
