import React ,{ useState } from 'react'
import { Link,useParams } from "react-router-dom";
import Nav from './Nav';
import { useProductsContext  } from '../context/ApiContext';
import { useCart } from '../context/CartContext'

export default function Cart() {

  const { products, loading } = useProductsContext();
  const { cart, removeFromCart, clearCart } = useCart();
 console.log('i',cart);

  /*cupon*/
  const updateQuantity = (itemId, newQuantity) => {
    const updatedCart = cart.map((item) => {
      if (item.id === itemId) {
        return { ...item, qty: newQuantity };
      }
      return item;
    });

    
  };
     

  



    return(<>
            
            <Nav/>

               


            <div className='main'>
            <h1>Shopping Cart</h1>

<div className="shopping-cart">
{cart.map((item, index) => (<>
  <div className="column-labels">
    <label className="product-image">Image</label>
    <label className="product-details">Product</label>
    <label className="product-price">Price</label>
    <label className="product-quantity">Quantity</label>
    <label className="product-removal">Remove</label>
    <label className="product-line-price">Total</label>
  </div>

  <div className="product" key={index} >
    <div className="product-image">
      <img src={item.image}/>
    </div>
    <div className="product-details">
      <div className="product-title">{item.name}</div>
      <p className="product-description">{item.dec}</p>
    </div>
    <div className="product-price">{item.price}</div>
    <div className="product-quantity">
    <input type="number" value={item.qty} onChange={(e) => updateQuantity(item.id, parseInt(e.target.value, 10))}
            />
    </div>
    <div className="product-removal">
      <button className="remove-product" onClick={() => removeFromCart(item)}>
        Remove
      </button>
    </div>
    <div className="product-line-price">{item.price * item.qty }</div>{console.log('qty',item.qty)}
  </div></>
))}
  
  
  <div className="totals">
    <div className="totals-item">
      <label>Subtotal</label>
      <div className="totals-value" id="cart-subtotal"></div>
    </div>
    <div className="totals-item">
      <label>Tax (5%)</label>
      <div className="totals-value" id="cart-tax">3.60</div>
    </div>
    <div className="totals-item">
      <label>Shipping</label>
      <div className="totals-value" id="cart-shipping">15.00</div>
    </div>
    <div className="totals-item totals-item-total">
      <label>Grand Total</label>
      <div className="totals-value" id="cart-total">90.57</div>
      
    </div>
    
  </div>
      
  <button className="checkout">Checkout</button>

</div>
            </div>
        </>)
}
