import React ,{ useState,useEffect } from 'react'
import { Link,NavLink,useParams } from "react-router-dom";
import Nav from './Nav';
import { useProductsContext  } from '../context/ApiContext';
import { useCopnContext  } from '../context/Copn';
import { useCart } from '../context/CartContext'

export default function Product({ product }) {
    const [activeIndex, setActiveIndex] = useState(0);
    const [couponInput, setCouponInput] = useState(''); // Note the corrected variable name
  const [appliedCoupon, setAppliedCoupon] = useState(null);
  const [item, setItem] = useState(null);
  const [aid, setAid] = useState('');
        const [aprice, setAprice] = useState('');
        const [asize, setAsize] = useState('');
      const [selectedSize, setSelectedSize] = useState('');
    const { productid } = useParams();
    const { products, loading } = useProductsContext();
    const { couponData } = useCopnContext();
    const { addToCart } = useCart();
    // Verify if productData is available
    const pid=parseInt(productid)
    const productt = products?.find((item) => item.id === pid);

    if (!productt) {
      return <p>Product not found.</p>;
    }
    if (loading) {
        return (
        <>
        <section className="lor-layout">
  <div className="lor-header"></div>
  <div className="lor-main"><span className="loader">Loading</span></div>
  <div className="lor-footer"></div>
</section></>)
      }
    
    

    const smallImgs = [
          productt.image,
          productt.imageone,
          productt.imagetwo,
          productt.imagethree,
        ];
    
      
    
      const handleSmallImgClick = (index) => {
        setActiveIndex(index);
      };
      
      const applyCoupon = () => {
        const coupon = couponData?.find((item) => item.code === couponInput);
        if (coupon) {
          const subs=productt.price-((productt.price*coupon.off)/100)

          setAppliedCoupon(subs)
          
          
          //setAppliedCoupon(subs);
          console.log('subs',appliedCoupon);
        } else {
          // Handle invalid coupon code
          alert('Invalid coupon code');
        }
      };
       

  const handleSizeChange = (event) => {
    setSelectedSize(event.target.value); // Update the selected size in state
  };
      


      const addtocartl =()=>{
        
        if(appliedCoupon){
          setAprice(appliedCoupon)
          setAid(productt.id)
          setAsize(selectedSize)
          const itemq = {
            id: aid,
            size: asize,
            price: aprice,
          image:productt.image,
          name:productt.name,
  
        }
        setItem(itemq)
        }
        else{
          setAprice(productt.price)
          setAid(productt.id)
          setAsize(selectedSize)
          const itemq = {
            id: aid,
            size: asize,
            price: aprice,
          image:productt.image,
          name:productt.name,
  
        }
        setItem(itemq)
        }
        
      
        addToCart(item);
      }

    return(
        <>
         <Nav/>
    
<section className="product-container main">
      
        <div className="img-card">
            <img id="featured-image"
        src={smallImgs[activeIndex]}
        alt="Featured Product" />
        
            <div className="small-Card">
            {smallImgs.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Small Product ${index}`}
            className={`small-Img ${index === activeIndex ? 'sm-card' : ''}`}
            onClick={() => handleSmallImgClick(index)}
          />))}
                </div>
        </div>
      
        <div className="product-info">
            <h3>{productt.name}</h3>
            
            <h5> Price: <del>{productt.price}</del> {appliedCoupon && (<span>Discounted Price: <del> {appliedCoupon}</del></span>)}</h5>

            <p>{productt.dec}</p>
            
            <div className="sizes">
                <p>Size:</p>
                <select name="Size" id="size" className="size-option" value={selectedSize}onChange={handleSizeChange}>
                    <option value="xxl">XXL</option>
                    <option value="xl">XL</option>
                    <option value="medium">Medium</option>
                    <option value="small">Small</option>
                </select>
            </div>

            <div className="quantity">
                <input type="number" value="1" min="1"/><button onClick={addtocartl}>Add to Cart</button>
                
            </div>

            <div className='cupon-box'>
                <input className='cupon' value={couponInput} onChange={(e) => setCouponInput(e.target.value)} placeholder='Cupon Code' /><Link onClick={applyCoupon} className='icon-btn'>Apply</Link>
            </div>
        </div>
    </section>





        </>
    )
}
