import React ,{ useState,useEffect } from 'react'
import { Link } from "react-router-dom";
import Nav from './Nav';

export default function Product() {

    


    const smallImgs = [
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955',
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-6_360x.jpg?v=1695035964',
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-5-27529864151190_360x.jpg?v=1695035962',
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035955',
        'https://www.libas.in/cdn/shop/files/green-solid-cotton-straight-kurta-with-palazzos-and-dupatta-libas-2-27529862938774_360x.jpg?v=1695035957',
      ];
    
      const [activeIndex, setActiveIndex] = useState(0);
    
      const handleSmallImgClick = (index) => {
        setActiveIndex(index);
      };

    return(
        <>
         <Nav/>
    
<section class="product-container">
      
        <div class="img-card">
            <img id="featured-image"
        src={smallImgs[activeIndex]}
        alt="Featured Product" />
        
            <div class="small-Card">
            {smallImgs.map((img, index) => (
          <img
            key={index}
            src={img}
            alt={`Small Product ${index}`}
            className={`small-Img ${index === activeIndex ? 'sm-card' : ''}`}
            onClick={() => handleSmallImgClick(index)}
          />))}
                </div>
        </div>
      
        <div class="product-info">
            <h3>yeh asli hai</h3>
            <h5>Price: 140 <del>170</del></h5>
            <p>tae corporis veniam atque facilis, et alias eos ullam nesciunt voluptatum.</p>
            
            <div class="sizes">
                <p>Size:</p>
                <select name="Size" id="size" class="size-option">
                    <option value="xxl">XXL</option>
                    <option value="xl">XL</option>
                    <option value="medium">Medium</option>
                    <option value="small">Small</option>
                </select>
            </div>

            <div class="quantity">
                <input type="number" value="1" min="1"/>
                <button>Add to Cart</button>
            </div>

            <div className='cupon-box'>
                <input className='cupon' placeholder='Cupon Code' /><Link className='icon-btn'>Apply</Link>
            </div>
        </div>
    </section>





        </>
    )
}
