import React, { createContext, useContext, useState } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

// Create a context for the cart
const CartContext = createContext();

// Create a CartProvider component to manage the cart state
const CartProvider = ({ children }) => {
  const [cartItems, setCartItems] = useState([]);
  const {suserData } = useAuth();
  const client = axios.create({
    baseURL: 'http://127.0.0.1:8000',
  });

  const addToCart = (item) => {
    setCartItems([...cartItems, item]);
    patchcart()
  };

  const removeFromCart = (item) => {
    const updatedCart = cartItems.filter((cartItem) => cartItem !== item);
    setCartItems(updatedCart);
    patchcart(updatedCart)
  };

  function getcart(e) {
    client.get(`/api/cart/${suserData.id}`,e).then(function (res) {
      console.log(res);
    });
  }

  function patchcart(e) {
    const user_id=suserData.id
    client.post(`/api/getcart/?user_id=${user_id}`,cartItems).then(function (res) {
      console.log(res);
    });
  }

  return (
    <CartContext.Provider value={{ cartItems, addToCart, removeFromCart }}>
      {children}
    </CartContext.Provider>
  );
};

// Create a custom hook to use the cart context in components
const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
export { CartProvider, useCart };
