import React, { createContext, useContext, useReducer, useCallback, useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth  } from '../context/AuthContext';

// Create a cart context
const CartContext = createContext();

// Define initial state for the cart
const initialCartState = [];
const initialFavState = [];

// Create a cart reducer to handle cart actions
const cartReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_CART':
      
      return [...state, action.payload];
    case 'REMOVE_FROM_CART':
      return state.filter(item => item.id !== action.payload.id);
    case 'CLEAR_CART':
      return [];
    default:
      return state;
  }
};
const favReducer = (state, action) => {
  switch (action.type) {
    case 'ADD_TO_FAV':
      return [...state, action.payload];
    case 'REMOVE_FROM_FAV':
      return state.filter(item => item.id !== action.payload.id);
    case 'CLEAR_FAV':
      return [];
    default:
      return state;
  }
};



// Create a CartProvider component to provide cart state and functions to child components
export const CartProvider = ({ children }) => {
  const [cart, dispatch] = useReducer(cartReducer, initialCartState);
  const [fav, spatch] = useReducer(favReducer, initialFavState);
  const [user_id, setUser_id] = useState();
  const [cartdata,setCartdata]=useState()
  const { suserData ,isloginned} = useAuth();


  useEffect(() => {
    if(isloginned) //get is login 
    getcart(suserData.id)//the reason of running befor  yess
  }, []);

  let uId=NaN;

  /*const addToCart = useCallback((item) => {
    dispatch({ type: 'ADD_TO_CART', payload: item });
    console.log(cart);
    cfun()
  }, []);*/

  const addToCart = (e) => {
    let newData;
  
    if (cartdata) {
      newData = {
        user_id: uId,
        item: [ e],
      };
      setCartdata(newData)
    } else {
      console.log('elas')
      newData = {
        user_id: uId,
        item: [e],
      };
      setCartdata(newData)
    }
  
    console.log(newData);
    cfun(newData);
  };
  /*const removeFromCart = useCallback((item) => {
    dispatch({ type: 'REMOVE_FROM_CART', payload: item });
  }, []);*/
  const removeFromCart = (e) => {
    /*let newData;
  
    if (cartdata) {
      newData = {
        user_id: uId,
        item: [...cartdata.item, e],
      };
      setCartdata(newData)
    } else {
      console.log('elas')
      newData = {
        user_id: uId,
        item: [e],
      };
      setCartdata(newData)
    }
  
    console.log(newData);*/
    dlecart();
  };

  const clearCart = useCallback(() => {
    dispatch({ type: 'CLEAR_CART' });
  }, []);

  const addToFav = useCallback((item) => {
    spatch({ type: 'ADD_TO_FAV', payload: item });
  }, []);

  const removeFromFav = useCallback((item) => {
    spatch({ type: 'REMOVE_FROM_FAV', payload: item });
  }, []);

  const clearFav = useCallback(() => {
    spatch({ type: 'CLEAR_FAV' });
  }, []);

  const client = axios.create({
    baseURL: 'http://127.0.0.1:8000',
  });

  
  

 

  const cfun = async(r) => {
    
    console.log(r);
    if (uId){
      console.log('cart',r);
      console.log('cartdata',cartdata);
      client.post(`/api/cart/`,r)
      .then((res) => {
        console.log('post res',res);
        getcart()
      })
      .catch((error) => console.error('Error fetching cart data:', error));}
  };
 
  const getcart =async()=>{
    //-----------------get suserData here ?"-----------------How?>>>  client.get(`/api/where you stored that data from "
    console.log('getcart', suserData);
    if(uId){
    client.get(`/api/getcart/?user_id=${uId}`).then((res) => {
      console.log('get res',res.data.items)
      setCartdata(res.data.items)
    })}
    
  }//ait solving profile first 


  const dlecart =async()=>{
    console.log('dlecart12',user_id);
    if(user_id){
      console.log('dlecart',uId);
    await client.delete(`/api/getcart/?user_id=${user_id}`).then((res) => {
      console.log('del res',res.data.items)
      getcart()
    })}
    
  }

  

 

  return (
    <CartContext.Provider value={{ fav, cart,cartdata, getcart, addToCart, removeFromCart, clearCart, addToFav, removeFromFav, clearFav }}>
      {children}
    </CartContext.Provider>
  );
};

  

// Create a custom hook to use the cart context
export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  

    // Cleanup function to clear cart when the component unmounts
    
  
  return context;
};
